import sharp from "sharp";
import { writeFileSync } from "fs";

const BG = "#0B1120";
const ACCENT = "#6366F1";

function makeSvg\(size, padding = 0\) {
  const innerSize = size - padding * 2;
  const cx = size / 2;
  const cy = size / 2;
  const letterSize = Math.round\(innerSize * 0.45\);
  const subtitleSize = Math.round\(innerSize * 0.09\);
  const subtitleY = cy + letterSize * 0.38 + subtitleSize + 4;

  // Bicycle-style "V" with decorative circle behind it
  const circleR = Math.round\(innerSize * 0.35\);

  return `<svg xmlns="http://www.w3.org/2000/svg" width="${size}" height="${size}" viewBox="0 0 ${size} ${size}">
  <rect width="${size}" height="${size}" fill="${BG}" rx="${Math.round\(size * 0.15\)}"/>

  <!-- Accent circle glow -->
  <circle cx="${cx}" cy="${cy - Math.round\(innerSize * 0.03\)}" r="${circleR}" fill="none" stroke="${ACCENT}" stroke-width="${Math.max\(2, Math.round\(size * 0.008\)\)}" opacity="0.35"/>

  <!-- Inner accent circle -->
  <circle cx="${cx}" cy="${cy - Math.round\(innerSize * 0.03\)}" r="${Math.round\(circleR * 0.72\)}" fill="none" stroke="${ACCENT}" stroke-width="${Math.max\(1, Math.round\(size * 0.005\)\)}" opacity="0.2"/>

  <!-- V letter -->
  <text x="${cx}" y="${cy + Math.round\(letterSize * 0.3\)}" 
        font-family="Arial, Helvetica, sans-serif" 
        font-size="${letterSize}" 
        font-weight="900" 
        fill="${ACCENT}" 
        text-anchor="middle" 
        dominant-baseline="middle"
        letter-spacing="-2">V</text>

  <!-- VELOCARD subtitle -->
  <text x="${cx}" y="${subtitleY}" 
        font-family="Arial, Helvetica, sans-serif" 
        font-size="${subtitleSize}" 
        font-weight="700" 
        fill="white" 
        text-anchor="middle" 
        opacity="0.7"
        letter-spacing="${Math.max\(1, Math.round\(subtitleSize * 0.25\)\)}">VELOCARD</text>
</svg>`;
}

async function generate\(\) {
  // icon-192x192.png
  const svg192 = Buffer.from\(makeSvg\(192, 0\)\);
  await sharp\(svg192\).png\(\).toFile\("C:/Users/hugom/Desktop/velo-card/public/icons/icon-192x192.png"\);
  console.log\("Created icon-192x192.png"\);

  // icon-512x512.png
  const svg512 = Buffer.from\(makeSvg\(512, 0\)\);
  await sharp\(svg512\).png\(\).toFile\("C:/Users/hugom/Desktop/velo-card/public/icons/icon-512x512.png"\);
  console.log\("Created icon-512x512.png"\);

  // icon-maskable.png \(512x512 with safe-zone padding ~20%\)
  const svgMask = Buffer.from\(makeSvg\(512, Math.round\(512 * 0.1\)\)\);
  await sharp\(svgMask\).png\(\).toFile\("C:/Users/hugom/Desktop/velo-card/public/icons/icon-maskable.png"\);
  console.log\("Created icon-maskable.png"\);
}

generate\(\).catch\(\(err\) => {
  console.error\("Error generating icons:", err\);
  process.exit\(1\);
}\);
SCRIPT)
